sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	g_form.showFieldMsg('u_request_type','newValue: ' + newValue, 'info');
	jslog('wbo new value: ' + newValue);
   if (newValue === '') {
      return;
   }
	//var requestType = g_form.getValue('u_request_type');
	var requestType = newValue;
	var initWhatNeeded = g_form.getValue('u_what_needed');

	g_form.clearOptions('u_what_needed');
	switch(requestType){
		case 'hr':
			g_form.addOption('u_what_needed','hr1','Human Resources1');
			g_form.addOption('u_what_needed','hr2','Human Resources2');
			break;
		case 'facilities':
			g_form.addOption('u_what_needed','facilities1','facilities1');
			g_form.addOption('u_what_needed','facilities2','facilities2');
			break;
		case 'legal':
			g_form.addOption('u_what_needed','legal1','Legal 1');
			g_form.addOption('u_what_needed','legal2','Legal 2');
			break;
	}
	g_form.addOption('u_what_needed','other','Other');   
	if (oldValue == newValue){
		g_form.setValue('u_what_needed',initWhatNeeded);
	}
}
