sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isPastDate: function(dateArg){
		var now = new GlideDateTime();
		var whenNeeded = new GlideDateTime(dateArg);
		gs.info('wbo needItUtils, datearg: ' + dateArg + ', now: ' + now + ',whenNeeded: ' + whenNeeded);

		if (whenNeeded.after(now)){
			return false;
		} else{
			return true;
		}
	},
	isDateToday: function(dateArg){
		var dateValidated = new GlideDateTime(dateArg);
		var today = new GlideDate();
		gs.info('Wbo isDateToday datevalidated: ' + dateValidated + ', value: ' + dateValidated.getValue() + ', today: ' + today);
		if (today.getValue() == dateValidated.getDate().getValue()){
			return true;
		} else{
			return false;
		}
	},
    type: 'NeedItUtils'
};
