sysauto_script.script:
var ga = new GlideRecord('x_58872_needit_needit');
var now = new GlideDateTime();
ga.addQuery('u_when_needed','<',now);
ga.query();
var returnString = '';
while(ga.next()){
	returnString += '' + ga.number + ' ';
}
if (ga.getRowCount() > 0)
	gs.info('WBO Overdue needIt Records: (total: ' + ga.getRowCount() + '): ' + returnString);
else
	gs.info('WBO no NeedIt Overdue recors found. ');
//---------------
var needItUtils = new NeedItUtils();
var soonOverdueNeedIt = needItUtils.soonOverdueNeedIt();
if (soonOverdueNeedIt.length > 0){
	gs.info('WBO soon overdue records: ' + soonOverdueNeedIt.join(','));
} else{
	gs.info('WBO no soon overdue records');
}
//-----------------
needItUtils.printOverdueNeedItTaskAndEvent();
needItUtils.printSoonOverdueNeedItTaskAndEvent();
